// input: single item from webhook
const body = items[0].json;
const product_id = body.product_id || null;
const reviews = body.reviews || [];
const out = [];

function splitSentences(text){
  if(!text) return [];
  // crude sentence splitter ‚Äî works well for short reviews
  const s = text.replace(/\n+/g,' ').trim();
  return s.match(/[^.!?]+[.!?]*/g) || [s];
}

for(const r of reviews){
  const sentences = splitSentences(r.text || "");
  for(const s of sentences){
    out.push({ json: {
      product_id,
      review_id: r.id,
      rating: r.rating,
      original_review: r.text,
      sentence: s.trim()
    }});
  }
}
return out;

// Parse raw body if it's a string, otherwise pass through
const raw = items[0].json.body ?? items[0].json; // support both shapes
let parsed;
try {
  parsed = (typeof raw === 'string') ? JSON.parse(raw.trim()) : raw;
} catch (err) {
  throw new Error('Webhook body is not valid JSON: ' + err.message);
}
return [{ json: parsed }];

const items = $input.all();

return items
  .filter(r =>
    typeof r.json.sequence === 'string' &&
    Array.isArray(r.json.labels) &&
    Array.isArray(r.json.scores) &&
    r.json.labels.length > 0 &&
    r.json.scores.length > 0
  )
  .map(r => {
    const seq = r.json.sequence;
    const labels = r.json.labels;
    const scores = r.json.scores;
    const idx = scores.indexOf(Math.max(...scores));
    return {
      json: {
        sentence: seq,
        label: labels[idx],
        label_score: scores[idx],
      }
    };
  });

const items = $input.all();
const groups = {};

for (const r of items) {
  // Skip entries that don't have a review_id or sentence
  if (!r.json.review_id || !r.json.sentence) {
    continue;
  }

  const id = r.json.review_id;
  if (!groups[id]) {
    groups[id] = {
      product_id: r.json.product_id,
      review_id: id,
      rating: r.json.rating,
      original_review: r.json.original_review,
      pros: [],
      cons: [],
      neutral: [],
    };
  }

  if (r.json.label === "pro") groups[id].pros.push(r.json.sentence);
  else if (r.json.label === "con") groups[id].cons.push(r.json.sentence);
  else groups[id].neutral.push(r.json.sentence);
}

return Object.values(groups).map(x => ({ json: x }));

const out = [];
for(const it of items){
  // HF may return string or {summary_text: "..."} or an array
  const r = it.json;
  let summary = "";
  if(typeof r === "string") summary = r;
  else if(r.summary_text) summary = r.summary_text;
  else if(Array.isArray(r) && r[0]?.summary_text) summary = r[0].summary_text;
  // If HTTP response content is nested in other fields, adapt accordingly
  out.push({ json: {
    product_id: it.json.product_id || it.json[0]?.product_id,
    review_id: it.json.review_id || it.json[0]?.review_id,
    pros: it.json.pros || it.json[0]?.pros || [],
    cons: it.json.cons || it.json[0]?.cons || [],
    neutral: it.json.neutral || it.json[0]?.neutral || [],
    summary: summary || (it.json.original_review || "")
  }});
}
return out;

// Two inputs should be connected to this Function node:
// 1. Original split reviews (with product_id, review_id, sentence, etc.)
// 2. Normalized classifier response (sentence, label, label_score)

const originals = $items("Normalize & explode reviews into sentence items");   // <-- match the *actual* node name exactly
const classified = $items("Normalize classifier response");

// Build a lookup by sentence
const lookup = {};
classified.forEach(c => {
  lookup[c.json.sentence] = {
    label: c.json.label,
    label_score: c.json.label_score
  };
});

// Merge
return originals.map(o => {
  const sentence = o.json.sentence;
  const classification = lookup[sentence] || { label: "neutral", label_score: 0 };
  return {
    json: {
      product_id: o.json.product_id,
      review_id: o.json.review_id,
      rating: o.json.rating,
      original_review: o.json.original_review,
      sentence: sentence,
      label: classification.label,
      label_score: classification.label_score
    }
  };
});

// Two inputs should be connected to this Function node:
// 1. The per-review objects (with pros, cons, etc.)
// 2. The summaries returned by HuggingFace (summary_text)

const reviews = $items("Aggregate sentences back to per-review pros/cons");  // <-- node where you grouped pros/cons/neutral
const summaries = $items("Summarization (per-review or per-product)");   // <-- HuggingFace summarization node

// Make sure they line up 1:1 by index
return reviews.map((rev, i) => {
  const summary = summaries[i]?.json.summary_text || "";
  return {
    json: {
      product_id: rev.json.product_id,
      review_id: rev.json.review_id,
      rating: rev.json.rating,
      original_review: rev.json.original_review,
      pros: rev.json.pros,
      cons: rev.json.cons,
      neutral: rev.json.neutral,
      summary: summary
    }
  };
});

const stop = new Set(["the","and","a","is","it","of","to","in","for","with","on","this","that","I","we","you"]);
function topKeywords(arr, n=5){
  const freq = {};
  for(const s of arr){
    for(const w of s.toLowerCase().split(/\W+/)){
      if(!w || stop.has(w) || w.length<3) continue;
      freq[w] = (freq[w]||0)+1;
    }
  }
  return Object.entries(freq).sort((a,b)=>b[1]-a[1]).slice(0,n).map(x=>x[0]);
}

const out=[];
for(const it of items){
  const pros = it.json.pros || [];
  const cons = it.json.cons || [];
  const candidates = topKeywords(pros.concat(cons), 5);
  const faqs = candidates.map(k => {
    const hasPro = pros.join(' ').toLowerCase().includes(k);
    const hasCon = cons.join(' ').toLowerCase().includes(k);
    let answer = "";
    if(hasPro && !hasCon) answer = `Users report positive experience with ${k}.`;
    else if(!hasPro && hasCon) answer = `Some users report problems with ${k}.`;
    else if(hasPro && hasCon) answer = `Mixed feedback on ${k}: positives include ${pros.slice(0,2).join(' ')}; complaints include ${cons.slice(0,2).join(' ')}.`;
    else answer = `Information about ${k} is limited in reviews.`;
    return { q: `How is ${k} of this product?`, a: answer };
  });
  // if no candidates, add fallback FAQ
  if(faqs.length===0){
    faqs.push({q:"What do customers like about this product?", a: pros.length? pros.join(' ') : "Customers praise the product quality."});
    faqs.push({q:"What issues do customers report?", a: cons.length? cons.join(' ') : "No consistent issues reported."});
  }
  it.json.faqs = faqs;
  out.push(it);
}
return out;

const results = [];
for(const it of items){
  const faqs = it.json.faqs || [];
  const mainEntity = faqs.map(f => ({
    "@type": "Question",
    "name": f.q,
    "acceptedAnswer": {
      "@type": "Answer",
      "text": f.a
    }
  }));
  const jsonLd = {
    "@context": "https://schema.org",
    "@type": "FAQPage",
    "mainEntity": mainEntity
  };
  results.push({ json: {
    product_id: it.json.product_id,
    review_id: it.json.review_id,
    pros: it.json.pros,
    cons: it.json.cons,
    summary: it.json.summary,
    faqs,
    faq_jsonld: jsonLd,
    faq_script: `<script type="application/ld+json">${JSON.stringify(jsonLd)}</script>`
  }});
}
return results;

